mapper.select:
SELECT * FROM lodge
WHERE latitude BETWEEN #{swLat} AND #{neLat}
AND	longitude BETWEEN #{swLng} AND #{neLng}
AND lodge_capacity >= #{peopleCnt}
AND stay_cost BETWEEN #{minPrice} AND #{maxPrice}

SELECT * FROM lodge
WHERE latitude between #{swLat} AND #{neLat}
AND	longitude BETWEEN #{swLng} AND #{neLng}
AND lodge_capacity >= #{peopleCnt}
