mapper.update:
UPDATE image
SET origin_name = #{origin_name },
	Stored_name = #{stored_name }
WHERE mem_no = #{mem_no }

mapper.select:
SELECT COUNT(*)
FROM image
WHERE mem_no = #{mem_no }

mapper.update:
UPDATE member
SET mem_name = #{mem_name },
	mem_pw = #{mem_pw },
	mem_phone = #{mem_phone },
	mem_intro = #{mem_intro }
WHERE mem_no = #{mem_no }
